import React, { useState } from 'react';

function CardTwo() {
  const [selectedType, setSelectedType] = useState('');

  return (
    <div className="flex flex-col h-full">
      {/* Centered Heading */}
      <h2 className="text-xl font-bold mb-4 text-center">Project Type</h2>

      {/* Centered Instruction text */}
      <p className="text-sm text-gray-600 mb-4 text-center">
        Don't panic â€“ you can customize these types in settings.
      </p>

      {/* Horizontal Navigation Bar for Project Types (without border) */}
      <div className="flex justify-center mb-4">
        {/* Time and Material Option */}
        <div
          className={`px-6 py-2 cursor-pointer rounded-l-lg ${
            selectedType === 'Time and Material'
              ? 'bg-blue-500 text-white'
              : 'bg-gray-200 text-gray-700'
          }`}
          onClick={() => setSelectedType('Time and Material')}
        >
          Time and Material
        </div>

        {/* Fixed Fee Option */}
        <div
          className={`px-6 py-2 cursor-pointer ${
            selectedType === 'Fixed Fee'
              ? 'bg-blue-500 text-white'
              : 'bg-gray-200 text-gray-700'
          }`}
          onClick={() => setSelectedType('Fixed Fee')}
        >
          Fixed Fee
        </div>

        {/* Non-billed Option */}
        <div
          className={`px-6 py-2 cursor-pointer rounded-r-lg ${
            selectedType === 'Non-billed'
              ? 'bg-blue-500 text-white'
              : 'bg-gray-200 text-gray-700'
          }`}
          onClick={() => setSelectedType('Non-billed')}
        >
          Non-billed
        </div>
      </div>

      {/* Input fields */}
      <label>Hourly</label>

      {/* Horizontal line below the Hourly label */}
      <div className="w-full h-[1px] bg-gray-300 mb-4"></div>

      {/* Hourly Input field */}
      <input
        type="number"
        className="mb-4 p-2 border rounded"
        placeholder="Enter hourly rate"
      />

      {/* Text explanation for Hourly Rate */}
      <p className="text-sm text-gray-600 mb-4">
        We need hourly rate to track your project billed amount.
      </p>

      <label>Budget</label>
      <input
        type="number"
        className="mb-4 p-2 border rounded"
        placeholder="Enter budget"
      />

      <label>Time Allocation</label>
      <input
        type="text"
        className="mb-4 p-2 border rounded"
        placeholder="Enter hours per person"
      />

      <label>Notes</label>
      <textarea
        className="p-2 border rounded"
        placeholder="Optional notes"
      />
    </div>
  );
}

export default CardTwo;
