import React, { useState } from 'react';
import CardOne from './CardOne';
import CardTwo from './CardTwo';
import CardThree from './CardThree';
import CardFour from './CardFour';

const MultiStepForm = () => {
  const [step, setStep] = useState(1);

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full">
        {step === 1 && <CardOne nextStep={nextStep} />}
        {step === 2 && <CardTwo nextStep={nextStep} prevStep={prevStep} />}
        {step === 3 && <CardThree nextStep={nextStep} prevStep={prevStep} />}
        {step === 4 && <CardFour prevStep={prevStep} />}
        <div className="flex justify-center mt-4 space-x-2">
          {[1, 2, 3, 4].map((s) => (
            <span
              key={s}
              className={`h-2 w-2 rounded-full ${step === s ? 'bg-blue-500' : 'bg-gray-300'}`}
            ></span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MultiStepForm;
