import React, { useState } from 'react';

function CardThree() {
  const [selectedCourses, setSelectedCourses] = useState({
    course1: false,
    course2: false,
    course3: false,
    course4: false,
    course5: false,
    course6: false,
  });

  const toggleCourse = (course) => {
    setSelectedCourses((prev) => ({
      ...prev,
      [course]: !prev[course],
    }));
  };

  const cancelCourse = (course) => {
    setSelectedCourses((prev) => ({
      ...prev,
      [course]: false,
    }));
  };

  return (
    <div className="flex flex-col h-full">
      {/* Main Heading */}
      <h2 className="text-2xl font-bold mb-4 text-center">Tasks</h2>

      {/* Add A Task Input and Button */}
      <label className="text-sm font-medium mb-2">Add A Task</label>

      {/* Input Field with Button in Same Row */}
      <div className="flex mb-4">
        <input
          type="text"
          className="p-2 border rounded w-full mr-2"
          placeholder="Enter task name"
        />

        <button className="p-2 bg-blue-500 text-white rounded hover:bg-blue-600 w-full">
          Add
        </button>
      </div>

      {/* Courses Section */}
      <div className="mt-4">
        <h3 className="text-lg font-semibold mb-2">Select Courses</h3>
        {['course1', 'course2', 'course3', 'course4', 'course5', 'course6'].map((course, index) => (
          <div key={index} className="flex items-center mb-2">
            {/* Checkbox */}
            <input
              type="checkbox"
              checked={selectedCourses[course]}
              onChange={() => toggleCourse(course)}
              className="mr-2"
            />

            {/* Course Name */}
            <span className="flex-1">{`Course ${index + 1}`}</span>

            {/* Check Icon for selected courses */}
            {selectedCourses[course] && (
              <span className="mr-2 text-green-500">âœ”</span>
            )}

            {/* Cancel Button */}
            <button
              onClick={() => cancelCourse(course)}
              className="text-red-500 text-xs"
            >
              Cancel
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CardThree;
